import React from "react";
import { useState } from "react";

function useLocalStorage(key, initialValue) {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(error);
      return initialValue;
    }
  });
  const setValue = (value) => {
    try {
      setStoredValue(value);
      window.localStorage.setItem(key, JSON.stringify(value));
    } catch (error) {
      console.error(error);
    }
  };
  return [storedValue, setValue];
}

const CookiesPolicy = () => {
  const [accept, setAssept] = useLocalStorage("Aceept", false);
  if (accept) {
    return null;
  }

  const handleAcceptClick = () => {
    setAssept(true);
  };

  return (
    <div className="fixed  bg-cookie bg-opacity-90 bottom-none p-s1 text-white text-basicD text-center rounded-t-frame ">
      <span>
        Мы используем cookie. Это позволяет нам анализировать взаимодействие с
        приложением и делать его лучше.
      </span>
      <div className="p-s05 border-solid border-white border-2 rounded-flag">
        <button onClick={handleAcceptClick}>Согласен</button>
      </div>
    </div>
  );
};

export default CookiesPolicy;
