import React from "react";
import { useState } from "react";

function useLocalStorage(key, initialValue) {
  const [storedValue, setStoredValue] =
    useState <
    T >
    (() => {
      try {
        const item = window.localStorage.getItem(key);
        return item ? JSON.parse(item) : initialValue;
      } catch (error) {
        console.error(error);
        return initialValue;
      }
    });
  const setValue = (value: T) => {
    try {
      setStoredValue(value);
      window.localStorage.setItem(key, JSON.stringify(value));
    } catch (error) {
      console.error(error);
    }
  };
  return [storedValue, setValue];
}

const CookiesPolicy = () => {
  const [accept, setAssept] = useLocalStorage("Aceept", false);
  if (accept) {
    return null;
  }

  const handleAcceptClick = () => {
    setAssept(true);
  };

  return (
    <div
      className={
        "fixed bottom-0 md:bottom-4 md:right-0 p-5 md:p-6 flex md:flex-col items-center md:items-start bg-dark-2 text-paper   max-w-md md:max-w-xs md:text-base bg-opacity-90 z-50 rounded-cookie md:rounded-cookieMd shadow-xl"
      }
    >
      <span className="leading-4 pr-8 md:p-0 md:mb-6 text-xs md:text-sm">
        Мы используем cookie. Это позволяет нам анализировать взаимодействие с
        картой и делать ее лучше.{" "}
      </span>
      <div>
        <button
          className="w-28 md:w-36 border rounded border-select hover:bg-dark-3 border-opacity-50 px-2.5 py-1.5 font-bold text-sm"
          onClick={handleAcceptClick}
        >
          Согласен
        </button>
      </div>
    </div>
  );
};

export default CookiesPolicy;
